import{_ as e,c as t,o,af as r}from"./chunks/framework.D2eNtXPh.js";const u=JSON.parse('{"title":"企业应用架构模式","description":"","frontmatter":{},"headers":[],"relativePath":"02_Architecture/02_Books/003_企业应用架构模式.md","filePath":"02_Architecture/02_Books/003_企业应用架构模式.md","lastUpdated":1768841158000}'),_={name:"02_Architecture/02_Books/003_企业应用架构模式.md"};function c(i,a,l,s,n,d){return o(),t("div",null,[...a[0]||(a[0]=[r('<h1 id="企业应用架构模式" tabindex="-1">企业应用架构模式 <a class="header-anchor" href="#企业应用架构模式" aria-label="Permalink to &quot;企业应用架构模式&quot;">​</a></h1><h2 id="_01-简介" tabindex="-1">01 简介 <a class="header-anchor" href="#_01-简介" aria-label="Permalink to &quot;01 简介&quot;">​</a></h2><blockquote><p>本书是一部系统总结企业级软件架构方法、思想和模式的著作，被誉为“软件架构领域最重要的10本书之一”。原书被翻译为葡萄牙语、波兰语、德语、俄语、韩语、日语、简体中文等多种语言，畅销不衰，并曾获得美国《软件开发》杂志颁发的“Jolt生产效率奖”。 第一部分（第1～8章）介绍企业应用架构领域需要掌握的关键知识和可能遇到的问题，并给出了这些问题的初步解决方案；第二部分（第9～18章）在对这些解决方案进行细化的同时，总结出了51种实用的软件架构模式，每个模式都详细给出了使用场景、解决方案、UML表示等。 这些模式从不同层次、不同侧面展示了什么是好的企业应用架构，以及如何设计好的企业应用架构，不管企业应用开发的技术和平台如何变化，这些模式始终能在企业应用架构中发挥重要的作用，是不可或缺的内功心法。</p></blockquote><h2 id="_02-ai速读" tabindex="-1">02 AI速读 <a class="header-anchor" href="#_02-ai速读" aria-label="Permalink to &quot;02 AI速读&quot;">​</a></h2><blockquote><p>本书是一部系统总结企业级软件架构方法、思想和模式的著作，被誉为“软件架构领域最重要的10本书之一”。原书被翻译为葡萄牙语、波兰语、德语、俄语、韩语、日语、简体中文等多种语言，畅销不衰，并曾获得美国《软件开发》杂志颁发的“Jolt生产效率奖”。 第一部分（第1～8章）介绍企业应用架构领域需要掌握的关键知识和可能遇到的问题，并给出了这些问题的初步解决方案；第二部分（第9～18章）在对这些解决方案进行细化的同时，总结出了51种实用的软件架构模式，每个模式都详细给出了使用场景、解决方案、UML表示等。 这些模式从不同层次、不同侧面展示了什么是好的企业应用架构，以及如何设计好的企业应用架构，不管企业应用开发的技术和平台如何变化，这些模式始终能在企业应用架构中发挥重要的作用，是不可或缺的内功心法。</p></blockquote>',5)])])}const p=e(_,[["render",c]]);export{u as __pageData,p as default};
